	def request_api(self, messages, temperature=None):
		text = self.tokenizer.apply_chat_template(
			messages,
			tokenize=False,
			add_generation_prompt=True
		)
		model_inputs = self.tokenizer([text], return_tensors="pt").to(self.model.device)

		if temperature is None:
			generated_ids = self.model.generate(
				**model_inputs,
				do_sample=False,
				# eos_token_id =self.tokenizer.eos_token_id,
				pad_token_id = self.tokenizer.eos_token_id,
				#temperature=0.0,
				max_new_tokens=300
			)
		else:
			generated_ids = self.model.generate(
				**model_inputs,
				temperature=temperature,
				pad_token_id = self.tokenizer.eos_token_id,
				max_new_tokens=300
			)

		generated_ids = [
			output_ids[len(input_ids):] for input_ids, output_ids in zip(model_inputs.input_ids, generated_ids)
		]

		response = self.tokenizer.batch_decode(generated_ids, skip_special_tokens=True)[0]
		return response

	def get_response(self, content, temperature= None):
		messages = [{"role": "user", "content": content}]
		return self.request_api(messages, temperature)

	def gen_multi_answers(self, prompts, temperature = 0.9):
		tip_text = []
		for x in prompts:
			tip_text.append(self.tokenizer.apply_chat_template([
				 # {"role": "system", "content": system_prompt},
				 {"role": "user", "content": x}], tokenize=False, add_generation_prompt=True))
		
		# print('大模型接受的:', prompts[0][:100])
		# print(tip_text)
		tip_inputs = self.tokenizer(tip_text, return_tensors="pt", padding=True, padding_side="left", add_special_tokens=False)
		prompt_length = tip_inputs["input_ids"].shape[-1]

		#tip_inputs = self.tokenizer(tip_text, return_tensors="pt", padding=True, padding_side="left", add_special_tokens=False)
		tip_inputs = {k: v.to(self.model.device) for k, v in tip_inputs.items()}

		# if temperature == 0.0 :
		# 	tip_completion_ids = self.model.generate(**tip_inputs, generation_config=self.generation_config)
		# else:
		tip_completion_ids = self.model.generate(**tip_inputs, generation_config=self.generation_config_temp)

		prompt_length = tip_inputs["input_ids"].shape[-1]
		completion_ids = tip_completion_ids[:, prompt_length:]
		answers = [self.tokenizer.decode(x).replace('<|endoftext|>', '').replace('<｜end▁of▁sentence｜>', '').replace('<|im_end|>', '') for x in completion_ids]
	
		return [[prompts[0], answers[0]]]

